import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../../assets/images/logo-principal.png';
import './Login.css';

const Login: React.FC = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async () => {
    try {
      const response = await fetch('http://localhost:8081/api/auth/login', { // Asegúrate de que la URL sea la correcta
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      if (response.ok) {
        const data = await response.json();
        alert(data.message); // Mostrar el mensaje de éxito

        // Almacenar el token en localStorage
        localStorage.setItem('authToken', data.token);

        // Redirigir a la página de inicio (o cualquier otra página que desees)
        navigate('/proyectos');
      } else {
        const message = await response.text();
        alert(message); // Mostrar el mensaje de error
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Error al iniciar sesión.');
    }
  };

  const handleLoginRedirect = () => {
    navigate('/registro'); // Redirige a la página de registro
  };

  return (
    <div className="m-4">
      {/* Logo en la esquina superior izquierda y enlace Registrarse en la esquina superior derecha */}
      <div className="d-flex justify-content-between align-items-start mt-3 px-4">
        <img src={logo} alt="Logo" className="logo" />
        <p className="m-4">
          <a
            href="#"
            className="link-body-emphasis link-offset-2 link-underline-opacity-25 link-underline-opacity-75-hover fs-4"
            onClick={handleLoginRedirect} // Llamada a la función de redirección
          >
            Registrarse
          </a>
        </p>
      </div>

      {/* Formulario de inicio de sesión en la parte inferior */}
      <div className="d-flex justify-content-center mt-5">
        <div className="login-box col-12 col-md-6">
          <h2>Iniciar sesión</h2>
          <div className="input-group">
            <label>Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Ingresa tu correo"
            />
          </div>
          <div className="input-group">
            <label>Contraseña</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Ingresa tu contraseña"
            />
          </div>
          <button onClick={handleLogin}>Iniciar sesión</button>
        </div>
      </div>
    </div>
  );
};

export default Login;
