import './Registrar.css';
import logo from '../../assets/images/logo.jpeg'; // Asegúrate de que la ruta sea correcta
import { useState, useEffect } from 'react'; // Importar useState y useEffect
import { useNavigate } from 'react-router-dom'; // Importar useNavigate para redirección

const Registrar = () => {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [roles, setRoles] = useState<any[]>([]);
    const [selectedRole, setSelectedRole] = useState('');
    const navigate = useNavigate(); // Hook de React Router para redirección

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        const userData = {
            nombre: name,
            email: email,
            password: password,
            id_rol: selectedRole,
        };

        try {
            const response = await fetch('http://localhost:8080/api/users/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(userData),
            });

            if (response.ok) {
                console.log('Usuario registrado');
                alert('Registro exitoso');
                setName('');
                setEmail('');
                setPassword('');
                setSelectedRole('');
            } else {
                console.log('Error al registrar usuario');
                alert('Error al registrar usuario');
            }
        } catch (error) {
            console.error('Error en la solicitud:', error);
            alert('Hubo un problema al registrar el usuario');
        }
    };

    const fetchRoles = async () => {
        try {
            const response = await fetch('http://localhost:8080/api/users/roles');
            if (response.ok) {
                const rolesData = await response.json();
                setRoles(rolesData);
            } else {
                console.error('Error al obtener los roles');
            }
        } catch (error) {
            console.error('Error al obtener los roles:', error);
        }
    };

    useEffect(() => {
        fetchRoles();
    }, []);

    const handleLoginRedirect = () => {
        navigate('/login'); // Redirige a la página de inicio de sesión
    };

    return (
        <div className='m-4'>
            <div className='row'>
                <div className='col-6'>
                    <img src={logo} alt="Logo" className="logo" />
                </div>
                <div className='col-6'>
                    <div className='d-flex flex-row-reverse'>
                        <div className="p-2">
                            <p>
                                <a
                                    href="#"
                                    className="link-body-emphasis link-offset-2 link-underline-opacity-25 link-underline-opacity-75-hover fs-4"
                                    onClick={handleLoginRedirect} // Llamada a la función de redirección
                                >
                                    Iniciar Sesion
                                </a>
                            </p>
                        </div>
                        <div className='p-2'>
                            <h4> | </h4>
                        </div>
                    </div>
                </div>
            </div>

            <div className="d-flex justify-content-center align-items-start vh-75 mt-5">
                <div className="form-container border border-dark rounded p-4 shadow-lg">
                    <h2 className="text-center text-dark mb-4">Registro</h2>
                    <form onSubmit={handleSubmit}>
                        <div className="mb-3">
                            <label htmlFor="name" className="form-label text-dark">Nombre</label>
                            <input
                                type="text"
                                className="form-control"
                                id="name"
                                placeholder="Ingresa tu nombre"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                            />
                        </div>

                        <div className="mb-3">
                            <label htmlFor="email" className="form-label text-dark">Email</label>
                            <input
                                type="email"
                                className="form-control"
                                id="email"
                                placeholder="Ingresa tu email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                            />
                        </div>

                        <div className="mb-3">
                            <label htmlFor="password" className="form-label text-dark">Contraseña</label>
                            <input
                                type="password"
                                className="form-control"
                                id="password"
                                placeholder="Crea una contraseña"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                        </div>

                        <div className="mb-3">
                            <label htmlFor="role" className="form-label text-dark">Rol</label>
                            <select
                                className="form-select"
                                id="role"
                                value={selectedRole}
                                onChange={(e) => setSelectedRole(e.target.value)}
                            >
                                <option value="">Selecciona un rol</option>
                                {roles.map((role) => (
                                    <option key={role.id_rol} value={role.id_rol}>
                                        {role.rol}
                                    </option>
                                ))}
                            </select>
                        </div>

                        <div className="text-center">
                            <button
                                type="submit"
                                className="btn btn-dark w-100"
                            >
                                Registrarme
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default Registrar;
